import React, { Component, useEffect, useState } from 'react';
import {
    StyleSheet, View, Modal,
    Text,
    TextInput,
    TouchableOpacity,
    Image,
    StatusBar,
    ScrollView,
    FlatList,
    Pressable,
    Alert,
} from 'react-native';
